import React from 'react';
import { Link } from 'react-router-dom';
import "./userAccount.css"

const UserAccount = () => {


    useEffect(() => {
        fetch(`http://localhost:4000/movies/${movie_id}`)
          .then(response => {
            if (!response.ok) {
              throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
          })
          .then(data => {
            console.log('Fetched movie details:', data);

            const selectedMovie = data[0];

            setMovie(selectedMovie);
          })
          .catch(error => console.error('Error fetching movie details:', error));
      }, [movie_id]);
  return (
    <div className='account'>
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-header">
              <h3>User Profile</h3>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <label htmlFor="first_name" className="form-label">First Name:</label>
                <input type="text" id="username" className="form-control"  readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="last_name" className="form-label">Last Name:</label>
                <input type="text" id="username" className="form-control"  readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="email_id" className="form-label">Email:</label>
                <input type="email" id="email" className="form-control"  readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="date_of_birth" className="form-label">Date of Birth:</label>
                <input type="date" id="username" className="form-control"  readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="mob_no" className="form-label">Mobile no:</label>
                <input type="number" id="username" className="form-control" placeholder='+91xxxxxxxxxx'  readOnly />
              </div>

              <div className='loginform'>
            want to Update  ?
            <Link to='/editAccount'>
              <span style={{color: "red", cursor:"pointer"}} >Edit Profile</span>
            </Link>
            </div>


            </div>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default UserAccount;
