import React, { useEffect, useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import "./Profile.css"

const Profile = () => {
  const { user_id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Retrieve user details from session storage
    const storedUserDetails = sessionStorage.getItem("userDetails");

    if (storedUserDetails) {
      const userDetails = JSON.parse(storedUserDetails);
      setUser(userDetails);
    } else {
      // If user details are not available in session storage, redirect to login page
      navigate('/signin');
    }
  }, [navigate]);

  if (!user) {
    // Handle case where user details are not available
    return <div>Loading...</div>;
  }

  return (
    <div className='account'>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="card">
              <div className="card-header">
                <h3>User Profile</h3>
              </div>
              <div className="card-body">
                <div className="user-details">
                  <p><strong>First Name :</strong> {user.first_name}</p>
                  <p><strong>Last Name :</strong> {user.last_name} </p>
                  <p><strong>Email :</strong> {user.email_id}</p>
                  <p><strong>Date of Birth :</strong> {user.date_of_birth}</p>
                  <p><strong>Mobile no :</strong> {user.mob_no}</p>
                  <p><strong>Role :</strong> {user.role}</p>
                </div>

                <div className='loginform'>
                  Want to Update?
                  <Link to='/editAccount'>
                    <span style={{ color: "red", cursor: "pointer" }}>Edit Profile</span>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
