import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const { user_id } = useParams();
  const [user, setUser] = useState([]);

  useEffect(() => {
    if (!user_id) {
      // Handle the case where user_id is not available (user not logged in, or other scenarios)
      return;
    }

    fetch(`http://localhost:4000/user/${user_id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched user details:', data);

        const selectedUser = data[0];

        setUser(selectedUser);
      })
      .catch((error) => console.error('Error fetching user details:', error));
  }, [user_id]);

  // If user_id is not available, you can render a message or redirect to the login page
  if (!user_id) {
    return (
      <div className="container mt-5">
        <p>Please log in to view your profile.</p>
        {/* You can also add a Link to the login page here */}
      </div>
    );
  }

  return (
    <div className="account">
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            {user.length > 0 && ( // Check if user data is available
              <div className="card">
                <div className="card-header">
                  <h3>User Profile</h3>
                </div>
                <div className="card-body">
                  <div className="user-details">
                    <p>
                      <strong>First Name :</strong> {user.first_name}
                    </p>
                    {/* ... (Other user details) */}
                  </div>

                  <div className="loginform">
                    Want to Update ?
                    <Link to="/editAccount">
                      <span style={{ color: 'red', cursor: 'pointer' }}>
                        Edit Profile
                      </span>
                    </Link>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
