import React, { useState, useEffect } from 'react';
import "./moviedetails.css"


function MovieDetails({match}) {
  const { movie_id } = match.params;
  const [movie, setMovie] = useState({});

  useEffect(() => {

    fetch(`http://localhost:4000/movies/${movie_id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setMovie(data))
      .catch(error => console.error('Error fetching movie details:', error));
  }, [movie_id]);

    return (
        <>
        <div className='moviedetails'>
        <div className="container mt-5 movie-container">
      <div className="row">
        <div className="col-md-6">
          <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
            <div className="carousel-inner">
              <div className="carousel-item active">
                <div className="carousel-card">
                  <img src={movie.image} className="d-block w-100" alt={movie.title} />
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="movie-details">
            cons
            <h2>{movie.title}</h2>
            {/* <p>Description: </p>
            <p>duration</p>
            <p>Language</p>
            <p>Release Date: January 1, 2023</p> */}

            <button className="btn btn-primary btn-book-now">Book Now</button>
          </div>
        </div>
      </div>
    </div>
      </div>
      </>
    )
}

export default MovieDetails;
