import React from 'react';
import './theaters.css';

function Theaters() {
    const theatersData = [
        {
          id: 1,
          name: 'Theater A',
          screens: [
            {
              id: 101,
              name: 'Screen 1',
              shows: [
                { id: 1001, time: '12:00 PM' },
                { id: 1002, time: '03:00 PM' },
                // Add more showtimes as needed
              ],
            },
            // Add more screens as needed
          ],
        },
        // Add more theaters as needed
      ];

  return (
    <div className='theater'>
        <div className='container mt-5'>
      {theatersData.map((theater) => (
        <div key={theater.id} className='card mb-4'>
          <div className='card-header'>
            <h2>{theater.name}</h2>
          </div>
          <div className='card-body'>
            {theater.screens.map((screen) => (
              <div key={screen.id} className='mb-3'>
                <h3>{screen.name}</h3>
                <ul className='list-group'>
                  {screen.shows.map((show) => (
                    <li key={show.id} className='list-group-item'>
                      {show.time}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>

    </div>
  );
}

export default Theaters;
