import React,{useState} from 'react';
import './theaters.css';

function Theaters() {

    const [selectedDate, setSelectedDate] = useState(new Date());

    const theatersData = [
        {
          id: 1,
          name: 'Theater A',
          screens: [
            {
              id: 101,
              name: 'Screen 1',
              shows: [
                { id: 1001, time: '12:00 PM' },
                { id: 1002, time: '03:00 PM' },
                // Add more showtimes as needed
              ],
            },
            {
              id: 102,
              name: 'Screen 2',
              shows: [
                { id: 1003, time: '01:30 PM' },
                { id: 1004, time: '04:30 PM' },
                // Add more showtimes as needed
              ],
            },
            // Add more screens as needed
          ],
        },
        // Add more theaters as needed
      ];



      const handleDateChange = (date) => {
        setSelectedDate(date);
        // You may want to fetch data for the selected date here
      };

  return (
    <div className='theater'>
         <div className='container mt-5'>
      <h2 className='mb-4'>Select Date: {selectedDate.toDateString()}</h2>
      <div className='row'>
        <div className='col-md-4'>
          <div className='card'>
            <div className='card-body'>
              <h2>Theaters</h2>
              {theatersData.map((theater) => (
                <div key={theater.id} className='mb-3'>
                  <h3>{theater.name}</h3>
                  {theater.screens.map((screen) => (
                    <div key={screen.id} className='mb-2'>
                      <h4>{screen.name}</h4>
                      <ul className='list-group'>
                        {screen.shows.map((show) => (
                          <li key={show.id} className='list-group-item'>
                            {show.time}
                          </li>
                        ))}
                      </ul>
                    </div>
                  ))}
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className='col-md-8'>
          {/* Add a date picker component here, and call handleDateChange when the date changes */}
          {/* Example: <DatePicker selected={selectedDate} onChange={handleDateChange} /> */}
        </div>
      </div>
    </div>
    </div>
  );
}

export default Theaters;
