import React from 'react';
import './theaters.css';

function Theaters() {
  const theatersData = [
    { id: 1, name: 'Theater A', screens: ['Screen 1', 'Screen 2'] },
    { id: 2, name: 'Theater B', screens: ['Screen 1', 'Screen 2', 'Screen 3'] },
  ];

  return (
    <div className='theater'>
         <Container className="mt-5">
      <h2>Theaters and Showtimes</h2>

      <div className="row">
        {theaters.map(theater => (
          <div key={theater.id} className="col-md-4 mb-4">
            <Card>
              <Card.Header>{theater.name}</Card.Header>
              <ListGroup variant="flush">
                <ListGroup.Item>Showtime: {theater.showtime}</ListGroup.Item>
                <ListGroup.Item>Date: {theater.date}</ListGroup.Item>
              </ListGroup>
            </Card>
          </div>
        ))}
      </div>
    </Container>

    </div>
  );
}

export default Theaters;
