import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import "./Profile.css"

const Profile = () => {
  const { user_id } = useParams();
  console.log('user_id:', user_id);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const storedUserDetails = sessionStorage.getItem("userDetails");

        if (storedUserDetails) {
          const userDetails = JSON.parse(storedUserDetails);
          setUser(userDetails);
          setLoading(false);
        } else {
          const response = await fetch(`http://localhost:4000/user/${user_id}`);

          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }

          const data = await response.json();

          if (data.length > 0) {
            const selectedUser = data[0];
            sessionStorage.setItem("userDetails", JSON.stringify(selectedUser));
            setUser(selectedUser);
          } else {
            console.error('No user details found.');

          }

          setLoading(false);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
        setLoading(false);
      }
    };

    fetchUserDetails();
  }, [user_id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='account'>
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="card">
              <div className="card-header">
                <h3>User Profile</h3>
              </div>
              <div className="card-body">
                <div className="user-details">
                  <p><strong>First Name :</strong> {user?.first_name}</p>
                  <p><strong>Last Name :</strong> {user?.last_name || 'N/A'} </p>
                  <p><strong>Email :</strong> {user?.email_id || 'N/A'}</p>
                  <p><strong>Date of Birth :</strong> {user?.date_of_birth || 'N/A'}</p>
                  <p><strong>Mobile no :</strong> {user?.mob_no || 'N/A'}</p>
                  <p><strong>Role :</strong> {user?.role || 'N/A'}</p>
                </div>

                <div className='loginform'>
                  Want to Update?
                  <Link to='/editAccount'>
                    <span style={{ color: "red", cursor: "pointer" }}>Edit Profile</span>
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
