import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './moviedetails.css';

function MovieDetails() {
  const [movieDetails, setMovieDetails] = useState({});
  const { movieId } = useParams();

  useEffect(() => {
    // Fetch movie details based on the movieId
    fetch(`http://localhost:4000/movies/${movieId}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setMovieDetails(data))
      .catch(error => console.error('Error fetching data:', error));
  }, [movieId]);

  return (
    <>
      <div className='moviedetails'>
        <div className="container mt-5 movie-container">
          <div className="row">
            <div className="col-md-6">
              <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
                <div className="carousel-inner">
                  <div className="carousel-item active">
                    <div className="carousel-card">
                      <img src={movieDetails.image} className="d-block w-100" alt="Movie Image" />
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="movie-details">
                <h2>{movieDetails.title}</h2>
                <p>Description: {movieDetails.description}</p>
                <p>Duration: {movieDetails.duration}</p>
                <p>Language: {movieDetails.language}</p>
                <p>Release Date: {movieDetails.release_date}</p>

                <button className="btn btn-primary btn-book-now">Book Now</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default MovieDetails;
