import React from 'react';
import './theaters.css';

function Theaters() {
  const theatersData = [
    { id: 1, name: 'Theater A', screens: ['Screen 1', 'Screen 2'] },
    { id: 2, name: 'Theater B', screens: ['Screen 1', 'Screen 2', 'Screen 3'] },
  ];

  return (
    <div className='theaters'>
      <div className="container mt-5">
        <h2 className="mb-4">Theaters</h2>
        <div className="row">
          {theatersData.map((theater) => (
            <div key={theater.id} className="col-md-6 mb-4">
              <div className="card">
                <div className="card-body">
                  <h3 className="card-title">{theater.name}</h3>
                  <select className="form-select mb-3" aria-label="Select Screen">
                    {theater.screens.map((screen) => (
                      <option key={screen} value={screen}>
                        {screen}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Theaters;
