import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import "./editProfile.css";

const EditAccount = () => {
  const { user_id } = useParams();
  const [user, setUser] = useState({
    first_name: user,
    last_name: "LastName",
    email_id: "sampleuser@example.com",
    date_of_birth: null, // Add the selected date of birth
    mob_no: "+91 xxxxxxxxxx"
    // Add more user information fields as needed
  });

  const handleUpdateProfile = () => {
    // Fetch method for updating user details
    fetch(`http://localhost:4000/user/${user_id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('User details updated successfully:', data);
        // Handle success, e.g., redirect or show a success message
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card-edit">
            <div className="card-header">
              <h3>Profile</h3>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <label htmlFor="first_name" className="form-label">First Name:</label>
                <input type="text" id="first_name" className="form-control" value={user.first_name} readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="last_name" className="form-label">Last Name:</label>
                <input type="text" id="last_name" className="form-control" value={user.last_name} readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="email_id" className="form-label">Email:</label>
                <input type="email" id="email_id" className="form-control" value={user.email_id} readOnly />
              </div>
              <div className="mb-3">
                <label htmlFor="date_of_birth" className="form-label">Date of Birth:</label>
                {/* ... (date_of_birth input code) */}
              </div>
              <div className="mb-3">
                <label htmlFor="mob_no" className="form-label">Mobile no:</label>
                <input type="number" id="mob_no" className="form-control" placeholder='+91 xxxxxxxxxx' readOnly />
              </div>

              {/* Add more user information fields as needed */}

              <button className="btn btn-primary" onClick={handleUpdateProfile}>Update Profile</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditAccount;
